mapper.select:
select userid from member where username=#{username}
and phone = #{phone}
and email1 = #{email1}
and email2 = #{email2}

select passwd from member where userid=#{userid}
and username=#{username}
and phone = #{phone}
and email1 = #{email1}
and email2 = #{email2}

mapper.update:
	  update member
     set passwd = #{passwd}

    where userid = #{userid}

mapper.select:
select count(1) 
from member 
where email1 = #{email1}
and email2 = #{email2}
